mapper.select:
SELECT ANIMALNAME, TYPESEQ, PLAYERID,to_char(BIRTH,'YYYY-MM-DD') as BIRTH, CONDITION, MONEY, logindate
FROM ANIMAL_INFO
WHERE playerId = #{playerId}

mapper.update:
UPDATE ANIMAL_INFO
SET MONEY = #{money}
WHERE PLAYERID = #{playerId}

UPDATE ANIMAL_INFO
SET MONEY = #{money}, CONDITION = #{condition}
WHERE PLAYERID = #{playerId}
